    async generateTestCases(code, errorType, fixes) {
        // à¸ªà¸£à¹‰à¸²à¸‡ test cases à¹€à¸žà¸·à¹ˆà¸­à¸›à¹‰à¸­à¸‡à¸à¸±à¸™ regression
        const prompt = `
