 * à¹€à¸Šà¸·à¹ˆà¸­à¸¡à¸•à¹ˆà¸­à¸à¸±à¸š git-memory system à¸—à¸µà¹ˆà¸¡à¸µà¸­à¸¢à¸¹à¹ˆà¹à¸¥à¹‰à¸§
 */

const fs = require('fs').promises;
const path = require('path');
const EventEmitter = require('events');
const crypto = require('crypto');

class AIMemoryProxy extends EventEmitter {
    constructor(options = {}) {
        super();
        this.gitMemoryPath = options.gitMemoryPath || path.join(__dirname, '../../git-memory');
        this.memoryPath = options.memoryPath || path.join(__dirname, '../../memory');
        this.dataPath = options.dataPath || path.join(__dirname, '../../data');
        this.maxMemorySize = options.maxMemorySize || 1000; // MB
        this.compressionEnabled = options.compression || true;
        this.encryptionEnabled = options.encryption || false;
        this.memoryCache = new Map();
        this.sharedData = new Map();
        this.subscribers = new Set();

        this.init();
    }

    async init() {
        try {
            // à¸ªà¸£à¹‰à¸²à¸‡à¹‚à¸Ÿà¸¥à¹€à¸”à¸­à¸£à¹Œà¸—à¸µà¹ˆà¸ˆà¸³à¹€à¸›à¹‡à¸™
            await this.ensureDirectories();

            // à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸—à¸µà¹ˆà¸¡à¸µà¸­à¸¢à¸¹à¹ˆ
            await this.loadExistingMemory();

            // à¹€à¸£à¸´à¹ˆà¸¡à¸£à¸°à¸šà¸š monitoring
            this.startMemoryMonitoring();

            console.log('ðŸ§  AI Memory Proxy initialized successfully');
            console.log(`ðŸ“ Git Memory Path: ${this.gitMemoryPath}`);
            console.log(`ðŸ’¾ Memory Path: ${this.memoryPath}`);
            console.log(`ðŸ“Š Data Path: ${this.dataPath}`);

            this.emit('ready');
        } catch (error) {
            console.error('âŒ Failed to initialize AI Memory Proxy:', error);
            throw error;
        }
    }

    async ensureDirectories() {
        const dirs = [this.gitMemoryPath, this.memoryPath, this.dataPath];
        for (const dir of dirs) {
            try {
                await fs.access(dir);
            } catch {
                await fs.mkdir(dir, { recursive: true });
                console.log(`ðŸ“ Created directory: ${dir}`);
            }
        }
    }

    async loadExistingMemory() {
        try {
            // à¹‚à¸«à¸¥à¸”à¸ˆà¸²à¸ git-memory
            const gitMemoryFiles = await this.scanDirectory(this.gitMemoryPath);
            console.log(`ðŸ” Found ${gitMemoryFiles.length} files in git-memory`);

            // à¹‚à¸«à¸¥à¸”à¸ˆà¸²à¸ memory
            const memoryFiles = await this.scanDirectory(this.memoryPath);
            console.log(`ðŸ” Found ${memoryFiles.length} files in memory`);

            // à¹‚à¸«à¸¥à¸”à¸ˆà¸²à¸ data
            const dataFiles = await this.scanDirectory(this.dataPath);
            console.log(`ðŸ” Found ${dataFiles.length} files in data`);

            // à¸£à¸§à¸¡à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”
            const totalFiles = gitMemoryFiles.length + memoryFiles.length + dataFiles.length;
            console.log(`ðŸ“š Total memory files loaded: ${totalFiles}`);

        } catch (error) {
            console.error('âŒ Error loading existing memory:', error);
        }
    }

    async scanDirectory(dirPath) {
        try {
            const files = [];
            const items = await fs.readdir(dirPath, { withFileTypes: true });

            for (const item of items) {
                const fullPath = path.join(dirPath, item.name);
                if (item.isDirectory()) {
                    const subFiles = await this.scanDirectory(fullPath);
                    files.push(...subFiles);
                } else if (item.isFile()) {
                    files.push(fullPath);
                }
            }

            return files;
        } catch (error) {
            return [];
        }
    }

    // à¸£à¸°à¸šà¸šà¸ˆà¸±à¸”à¸à¸²à¸£à¸„à¸§à¸²à¸¡à¸ˆà¸³ AI
    async storeMemory(key, data, options = {}) {
        try {
            const memoryId = this.generateMemoryId(key);
            const timestamp = new Date().toISOString();

            const memoryObject = {
                id: memoryId,
                key,
                data,
                timestamp,
                type: options.type || 'general',
                priority: options.priority || 'normal',
                tags: options.tags || [],
                metadata: options.metadata || {}
            };

            // à¹€à¸à¹‡à¸šà¹ƒà¸™ cache
            this.memoryCache.set(memoryId, memoryObject);

            // à¹€à¸à¹‡à¸šà¹ƒà¸™à¹„à¸Ÿà¸¥à¹Œ
            await this.persistMemory(memoryObject);

            // à¹à¸ˆà¹‰à¸‡à¹€à¸•à¸·à¸­à¸™ subscribers
            this.emit('memoryStored', memoryObject);

            console.log(`ðŸ’¾ Memory stored: ${key} (${memoryId})`);
            return memoryId;

        } catch (error) {
            console.error('âŒ Error storing memory:', error);
            throw error;
        }
    }

    async retrieveMemory(key) {
        try {
            // à¸„à¹‰à¸™à¸«à¸²à¹ƒà¸™ cache à¸à¹ˆà¸­à¸™
            for (const [id, memory] of this.memoryCache) {
                if (memory.key === key) {
                    console.log(`ðŸ” Memory retrieved from cache: ${key}`);
                    return memory;
                }
            }

            // à¸„à¹‰à¸™à¸«à¸²à¹ƒà¸™à¹„à¸Ÿà¸¥à¹Œ
            const memory = await this.loadMemoryFromFile(key);
            if (memory) {
                this.memoryCache.set(memory.id, memory);
                console.log(`ðŸ” Memory retrieved from file: ${key}`);
                return memory;
            }

            console.log(`â“ Memory not found: ${key}`);
            return null;

        } catch (error) {
            console.error('âŒ Error retrieving memory:', error);
            return null;
        }
    }

    async searchMemory(query, options = {}) {
        try {
            const results = [];
            const searchTerms = query.toLowerCase().split(' ');

            // à¸„à¹‰à¸™à¸«à¸²à¹ƒà¸™ cache
            for (const [id, memory] of this.memoryCache) {
                if (this.matchesSearch(memory, searchTerms, options)) {
                    results.push(memory);
                }
            }

            // à¹€à¸£à¸µà¸¢à¸‡à¸¥à¸³à¸”à¸±à¸šà¸•à¸²à¸¡à¸„à¸§à¸²à¸¡à¹€à¸à¸µà¹ˆà¸¢à¸§à¸‚à¹‰à¸­à¸‡
            results.sort((a, b) => {
                const scoreA = this.calculateRelevanceScore(a, searchTerms);
                const scoreB = this.calculateRelevanceScore(b, searchTerms);
                return scoreB - scoreA;
            });

            console.log(`ðŸ” Search completed: "${query}" - ${results.length} results`);
            return results.slice(0, options.limit || 50);

        } catch (error) {
            console.error('âŒ Error searching memory:', error);
            return [];
        }
    }

    // à¸£à¸°à¸šà¸šà¹à¸Šà¸£à¹Œà¸‚à¹‰à¸­à¸¡à¸¹à¸¥
    async shareData(dataId, recipients, permissions = {}) {
        try {
            const shareId = this.generateShareId();
            const timestamp = new Date().toISOString();

            const shareObject = {
                id: shareId,
                dataId,
                recipients: Array.isArray(recipients) ? recipients : [recipients],
                permissions: {
                    read: permissions.read !== false,
                    write: permissions.write || false,
                    delete: permissions.delete || false,
                    share: permissions.share || false
                },
                timestamp,
                expiresAt: permissions.expiresAt || null
            };

            this.sharedData.set(shareId, shareObject);

            // à¸šà¸±à¸™à¸—à¸¶à¸à¸à¸²à¸£à¹à¸Šà¸£à¹Œ
            await this.persistShare(shareObject);

            // à¹à¸ˆà¹‰à¸‡à¹€à¸•à¸·à¸­à¸™ recipients
            this.notifyRecipients(shareObject);

            console.log(`ðŸ¤ Data shared: ${dataId} -> ${recipients.join(', ')}`);
            return shareId;

        } catch (error) {
            console.error('âŒ Error sharing data:', error);
            throw error;
        }
    }

    async getSharedData(shareId, requesterId) {
        try {
            const share = this.sharedData.get(shareId);
            if (!share) {
                throw new Error('Share not found');
            }

            // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸šà¸ªà¸´à¸—à¸˜à¸´à¹Œ
            if (!share.recipients.includes(requesterId)) {
                throw new Error('Access denied');
            }

            // à¸•à¸£à¸§à¸ˆà¸ªà¸­à¸šà¸§à¸±à¸™à¸«à¸¡à¸”à¸­à¸²à¸¢à¸¸
            if (share.expiresAt && new Date() > new Date(share.expiresAt)) {
                throw new Error('Share expired');
            }

            // à¸”à¸¶à¸‡à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸ˆà¸£à¸´à¸‡
            const data = await this.retrieveMemory(share.dataId);

            console.log(`ðŸ“¤ Shared data accessed: ${shareId} by ${requesterId}`);
            return {
                share,
                data,
                permissions: share.permissions
            };

        } catch (error) {
            console.error('âŒ Error accessing shared data:', error);
            throw error;
        }
    }

    // à¸£à¸°à¸šà¸š Subscription à¸ªà¸³à¸«à¸£à¸±à¸š real-time updates
    subscribe(subscriberId, filters = {}) {
        const subscription = {
            id: subscriberId,
            filters,
            timestamp: new Date().toISOString()
        };

        this.subscribers.add(subscription);
        console.log(`ðŸ“¡ New subscriber: ${subscriberId}`);

        return () => {
            this.subscribers.delete(subscription);
            console.log(`ðŸ“¡ Subscriber removed: ${subscriberId}`);
        };
    }

    // Helper methods
    generateMemoryId(key) {
        return crypto.createHash('sha256').update(key + Date.now()).digest('hex').substring(0, 16);
    }

    generateShareId() {
        return crypto.randomBytes(16).toString('hex');
    }

    async persistMemory(memoryObject) {
        const filePath = path.join(this.memoryPath, `${memoryObject.id}.json`);
        await fs.writeFile(filePath, JSON.stringify(memoryObject, null, 2));
    }

    async persistShare(shareObject) {
        const filePath = path.join(this.dataPath, 'shares', `${shareObject.id}.json`);
        await fs.mkdir(path.dirname(filePath), { recursive: true });
        await fs.writeFile(filePath, JSON.stringify(shareObject, null, 2));
    }

    async loadMemoryFromFile(key) {
        try {
            const files = await fs.readdir(this.memoryPath);
            for (const file of files) {
                if (file.endsWith('.json')) {
                    const filePath = path.join(this.memoryPath, file);
                    const content = await fs.readFile(filePath, 'utf8');
                    const memory = JSON.parse(content);
                    if (memory.key === key) {
                        return memory;
                    }
                }
            }
            return null;
        } catch (error) {
            return null;
        }
    }

    matchesSearch(memory, searchTerms, options) {
        const searchText = (
            memory.key + ' ' + 
            JSON.stringify(memory.data) + ' ' + 
            memory.tags.join(' ')
        ).toLowerCase();

        return searchTerms.every(term => searchText.includes(term));
    }

    calculateRelevanceScore(memory, searchTerms) {
        let score = 0;
        const text = memory.key.toLowerCase();

        searchTerms.forEach(term => {
            if (text.includes(term)) {
                score += term.length;
            }
        });

        return score;
    }

    notifyRecipients(shareObject) {
        this.emit('dataShared', shareObject);

        // à¹à¸ˆà¹‰à¸‡à¹€à¸•à¸·à¸­à¸™ subscribers à¸—à¸µà¹ˆà¹€à¸à¸µà¹ˆà¸¢à¸§à¸‚à¹‰à¸­à¸‡
        for (const subscriber of this.subscribers) {
            if (shareObject.recipients.includes(subscriber.id)) {
                this.emit('notification', {
                    subscriberId: subscriber.id,
                    type: 'dataShared',
                    data: shareObject
                });
            }
        }
    }

    startMemoryMonitoring() {
        setInterval(() => {
            this.cleanupExpiredShares();
            this.optimizeMemoryCache();
        }, 60000); // à¸—à¸¸à¸ 1 à¸™à¸²à¸—à¸µ

        console.log('ðŸ”„ Memory monitoring started');
    }

    async cleanupExpiredShares() {
        const now = new Date();
        let cleanedCount = 0;

        for (const [shareId, share] of this.sharedData) {
            if (share.expiresAt && now > new Date(share.expiresAt)) {
                this.sharedData.delete(shareId);
                cleanedCount++;
            }
        }

        if (cleanedCount > 0) {
            console.log(`ðŸ§¹ Cleaned up ${cleanedCount} expired shares`);
        }
    }

    optimizeMemoryCache() {
        if (this.memoryCache.size > this.maxMemorySize) {
            const entries = Array.from(this.memoryCache.entries());
            entries.sort((a, b) => new Date(a[1].timestamp) - new Date(b[1].timestamp));

            const toRemove = entries.slice(0, Math.floor(this.memoryCache.size * 0.2));
            toRemove.forEach(([id]) => this.memoryCache.delete(id));

            console.log(`ðŸ—‘ï¸ Optimized memory cache: removed ${toRemove.length} old entries`);
        }
    }

    // API à¸ªà¸³à¸«à¸£à¸±à¸šà¸à¸²à¸£à¹ƒà¸Šà¹‰à¸‡à¸²à¸™
    async getStats() {
        return {
            memoryCache: this.memoryCache.size,
            sharedData: this.sharedData.size,
            subscribers: this.subscribers.size,
            timestamp: new Date().toISOString()
        };
    }

    async exportMemory(format = 'json') {
        const memories = Array.from(this.memoryCache.values());

        if (format === 'json') {
            return JSON.stringify(memories, null, 2);
        }

        // à¸£à¸­à¸‡à¸£à¸±à¸š format à¸­à¸·à¹ˆà¸™à¹† à¹ƒà¸™à¸­à¸™à¸²à¸„à¸•
        return memories;
    }
}

