  // ========== AI Request Handling ==========

  async processAIRequest(request) {
    const requestId = this.generateRequestId();
    this.activeRequests.add(requestId);

    try {
      console.log(`ðŸ¤– Processing AI request: ${request.type}`);

      // à¹€à¸¥à¸·à¸­à¸ AI model à¸—à¸µà¹ˆà¹€à¸«à¸¡à¸²à¸°à¸ªà¸¡à¸—à¸µà¹ˆà¸ªà¸¸à¸”
      const selectedModel = await this.selectBestModel(request);

      // à¸£à¸§à¸šà¸£à¸§à¸¡ context à¸ˆà¸²à¸ MCP servers à¹à¸¥à¸° Git Memory
      const context = await this.gatherContext(request);

      // à¸›à¸£à¸°à¸¡à¸§à¸¥à¸œà¸¥à¸„à¸³à¸‚à¸­à¸”à¹‰à¸§à¸¢ AI
      const result = await this.executeAIRequest(selectedModel, request, context);

      // à¸šà¸±à¸™à¸—à¸¶à¸à¸œà¸¥à¸¥à¸±à¸žà¸˜à¹Œà¹à¸¥à¸°à¹€à¸£à¸µà¸¢à¸™à¸£à¸¹à¹‰
      await this.recordResult(request, result, selectedModel);

      this.activeRequests.delete(requestId);
      return result;

    } catch (error) {
      console.error(`âŒ AI request failed:`, error);
      this.activeRequests.delete(requestId);
      throw error;
    }
  }

  async selectBestModel(request) {
    const requestType = request.type;
    const availableModels = this.config.specializedAIs[requestType] || this.config.aiModels;

    // à¹€à¸¥à¸·à¸­à¸à¹‚à¸¡à¹€à¸”à¸¥à¸•à¸²à¸¡à¸›à¸£à¸°à¸ªà¸´à¸—à¸˜à¸´à¸ à¸²à¸žà¹à¸¥à¸°à¸„à¸§à¸²à¸¡à¹€à¸«à¸¡à¸²à¸°à¸ªà¸¡
    let bestModel = availableModels[0];
    let bestScore = 0;

    for (const model of availableModels) {
      const modelInfo = this.aiModelPool.get(model);
      if (!modelInfo || modelInfo.status !== 'ready') continue;

      // à¸„à¸³à¸™à¸§à¸“à¸„à¸°à¹à¸™à¸™à¸•à¸²à¸¡à¸«à¸¥à¸²à¸¢à¸›à¸±à¸ˆà¸ˆà¸±à¸¢
      const score = this.calculateModelScore(modelInfo, request);

      if (score > bestScore) {
        bestScore = score;
        bestModel = model;
      }
    }

    console.log(`ðŸŽ¯ Selected model: ${bestModel} (score: ${bestScore.toFixed(2)})`);
    return bestModel;
  }

  calculateModelScore(modelInfo, request) {
    let score = 100; // Base score

    // à¸¥à¸”à¸„à¸°à¹à¸™à¸™à¸•à¸²à¸¡ usage (load balancing)
    score -= modelInfo.usage * 0.1;

    // à¹€à¸žà¸´à¹ˆà¸¡à¸„à¸°à¹à¸™à¸™à¸–à¹‰à¸²à¹€à¸›à¹‡à¸™ specialization
    if (modelInfo.specializations.includes(request.type)) {
      score += 50;
    }

    // à¸¥à¸”à¸„à¸°à¹à¸™à¸™à¸•à¸²à¸¡ response time
    score -= modelInfo.averageResponseTime * 0.01;

    // à¹€à¸žà¸´à¹ˆà¸¡à¸„à¸°à¹à¸™à¸™à¸–à¹‰à¸²à¹ƒà¸Šà¹‰à¸‡à¸²à¸™à¸¥à¹ˆà¸²à¸ªà¸¸à¸” (warm model)
    const timeSinceLastUse = Date.now() - modelInfo.lastUsed;
    if (timeSinceLastUse < 60000) { // 1 minute
      score += 20;
    }

    return Math.max(0, score);
  }

  async gatherContext(request) {
    console.log('ðŸ“Š Gathering context from all sources...');
    const context = {
      project: {},
      git: {},
      mcp: {},
      user: {},
      history: []
    };

    try {
