#!/usr/bin/env node

/**
 * NEXUS IDE - Real-time Collaboration Server Launcher
 * Advanced collaborative development environment with AI assistance
 */

const fs = require('fs');
const path = require('path');
const { spawn } = require('child_process');

// Configuration
const CONFIG = {
    port: 3011,
    host: 'localhost',
    environment: 'development',
    logLevel: 'info'
};

// Colors for console output
const colors = {
    reset: '\x1b[0m',
    bright: '\x1b[1m',
    red: '\x1b[31m',
    green: '\x1b[32m',
    yellow: '\x1b[33m',
    blue: '\x1b[34m',
    magenta: '\x1b[35m',
    cyan: '\x1b[36m'
};

// Logging utility
function log(level, message, data = null) {
    const timestamp = new Date().toISOString();
    const colorMap = {
        info: colors.blue,
        success: colors.green,
        warning: colors.yellow,
        error: colors.red,
        debug: colors.cyan
    };
    
    const color = colorMap[level] || colors.reset;
    console.log(`${color}[${timestamp}] [${level.toUpperCase()}] ${message}${colors.reset}`);
    
    if (data) {
        console.log(`${colors.cyan}${JSON.stringify(data, null, 2)}${colors.reset}`);
    }
}

// Check if required files exist
function checkDependencies() {
    log('info', '🔍 Checking dependencies...');
    
    const requiredFiles = [
        'collaboration-server.js',
        'collaboration-client.js',
        'collaboration-demo.html'
    ];
    
    const missingFiles = [];
    
    for (const file of requiredFiles) {
        const filePath = path.join(__dirname, file);
        if (!fs.existsSync(filePath)) {
            missingFiles.push(file);
        }
    }
    
    if (missingFiles.length > 0) {
        log('error', '❌ Missing required files:', missingFiles);
        return false;
    }
    
    log('success', '✅ All dependencies found');
    return true;
}

// Create necessary directories
function createDirectories() {
    log('info', '📁 Creating necessary directories...');
    
    const directories = [
        'logs',
        'sessions',
        'documents',
        'uploads'
    ];
    
    directories.forEach(dir => {
        const dirPath = path.join(__dirname, dir);
        if (!fs.existsSync(dirPath)) {
            fs.mkdirSync(dirPath, { recursive: true });
            log('success', `✅ Created directory: ${dir}`);
        }
    });
}

// Start the collaboration server
function startCollaborationServer() {
    log('info', '🚀 Starting NEXUS IDE Collaboration Server...');
    
    try {
        // Import and start the collaboration server
        const CollaborationServer = require('./collaboration-server');
        
        const server = new CollaborationServer({
            port: CONFIG.port,
            host: CONFIG.host,
            cors: {
                origin: '*',
                methods: ['GET', 'POST', 'PUT', 'DELETE'],
                allowedHeaders: ['Content-Type', 'Authorization']
            },
            websocket: {
                pingTimeout: 60000,
                pingInterval: 25000,
                maxHttpBufferSize: 1e6
            },
            ai: {
                enabled: true,
                models: ['gpt-4', 'claude-3', 'llama-2'],
                maxTokens: 4096
            },
            collaboration: {
                maxParticipants: 50,
                maxDocuments: 100,
                autoSave: true,
                saveInterval: 30000
            },
            security: {
                rateLimit: {
                    windowMs: 15 * 60 * 1000, // 15 minutes
                    max: 1000 // limit each IP to 1000 requests per windowMs
                },
                maxFileSize: '10mb',
                allowedFileTypes: ['.js', '.ts', '.py', '.java', '.cpp', '.c', '.html', '.css', '.json', '.md']
            },
            logging: {
                level: CONFIG.logLevel,
                file: path.join(__dirname, 'logs', 'collaboration.log')
            }
        });
        
        // Setup event handlers
        server.on('started', (data) => {
            log('success', '🎉 Collaboration Server started successfully!');
            log('info', `📡 Server running on: http://${data.host}:${data.port}`);
            log('info', `🌐 Demo available at: http://${data.host}:${data.port}/demo`);
            log('info', `📊 Admin panel at: http://${data.host}:${data.port}/admin`);
            
            console.log(`\n${colors.bright}${colors.green}🚀 NEXUS IDE Collaboration Server${colors.reset}`);
            console.log(`${colors.cyan}📊 Server Status:${colors.reset}`);
            console.log(`   ├── Port: ${data.port}`);
            console.log(`   ├── Environment: ${process.env.NODE_ENV || 'development'}`);
            console.log(`   ├── WebSocket: Enabled`);
            console.log(`   └── AI Integration: Enabled`);
            console.log(`\n${colors.cyan}🔗 Available Endpoints:${colors.reset}`);
            console.log(`   ├── Health Check: http://${data.host}:${data.port}/health`);
            console.log(`   ├── API Docs: http://${data.host}:${data.port}/api-docs`);
            console.log(`   ├── WebSocket: ws://${data.host}:${data.port}`);
            console.log(`   └── Collaboration Demo: http://${data.host}:${data.port}/demo`);
            console.log(`\n${colors.cyan}🎯 WebSocket Events:${colors.reset}`);
            console.log('   ├── Connection: connect, disconnect');
            console.log('   ├── Sessions: join-session, leave-session');
            console.log('   ├── Documents: document-change, cursor-change');
            console.log('   ├── Chat: chat-message, voice-chat');
            console.log('   ├── Sharing: screen-share, file-share');
            console.log('   └── AI: ai-request, ai-response');
            console.log(`\n${colors.bright}${colors.green}✨ Ready for real-time collaboration!${colors.reset}\n`);
        });
        
        // Error handling
        server.on('error', (error) => {
            log('error', '❌ Server error:', error);
        });
        
        // Start the server
        server.start();
        
    } catch (error) {
        log('error', '💥 Failed to start Collaboration Server:', error);
        process.exit(1);
    }
}

// Graceful shutdown handling
process.on('SIGTERM', () => {
    log('info', 'SIGTERM received, shutting down gracefully...');
    if (server && server.close) {
        server.close(() => {
            log('info', 'Server closed successfully');
            process.exit(0);
        });
    } else {
        process.exit(0);
    }
});

process.on('SIGINT', () => {
    log('info', 'SIGINT received, shutting down gracefully...');
    if (server && server.close) {
        server.close(() => {
            log('info', 'Server closed successfully');
            process.exit(0);
        });
    } else {
        process.exit(0);
    }
});

// Handle uncaught exceptions
process.on('uncaughtException', (error) => {
    log('error', 'Uncaught Exception:', error);
    process.exit(1);
});

// Handle unhandled promise rejections
process.on('unhandledRejection', (reason, promise) => {
    log('error', 'Unhandled Rejection at:', promise, 'reason:', reason);
    process.exit(1);
});

// Start the application
startCollaborationServer();

